var Api = require('./lib/api');

module.exports = youtube;

/**
 * The class instance for YouTube API
 * @param  {Object} options The object options to gather data from YouTube Service
 *                          This object is required and it should have 2 properties
 *                          	*api 	The API Key for YouTube Data API
 *                          	*uri	The URI root target to make API calls
 *                          			Example: https://www.googleapis.com/youtube/v3/
 * @return {Instance}       The instance with the services and methods
 */
function youtube(options){
	if(options === undefined)
		throw new Error('Options parameter is missing');

	if(options.api === undefined)
		throw new Error('Api key is required');

	if(options.uri === undefined)
		throw new Error('URI target is required');

	var api = new Api(options);

	return api;
}