var api = require('./index'),
	fs = require('fs');

var config = JSON.parse(fs.readFileSync('./config.json'));

var youtube = api({
	api: config.apiKey,
	uri: config.apiUri
});

youtube.channels().list({
	part: 'contentDetails',
	id: 'UCsLiV4WJfkTEHH0b9PmRklw'
}).then(function(data){
	console.log(data);
})